import React, { useContext} from "react";
import PropTypes from "prop-types";
import RecipeContext from '../../context/recipe/recipeContext'

export const RecipeItem = ({ recipe }) => {

const recipeContext = useContext(RecipeContext)

  const { _id, img, title, ingredients, method, serves, time } = recipe;
  const { deleteRecipe, setCurrent, clearCurrent } = recipeContext

  const onDelete = () => {
    deleteRecipe(_id);
    clearCurrent();
  }

  return (
    <div className='row'>
      <div className='col s10 offset-s1'>
        <div className='card'>
          <div class='card-image'>
            <img className='activator' src={img}></img>
          </div>
          <div className='card-content'>
            <span className='card-title activator'>{title}</span>
          </div>
          <div className='card-reveal'>
            <span className='card-title activator'>
              {title}
              <i class=' far fa-window-close right'></i>
            </span>
            <p>Serves : {serves}</p>
            <p>Time (hrs) : {time}</p>
            <ul>
              {ingredients.map((ingredient) => (
                <li key={ingredient.id}>{ingredient.name + " : " + ingredient.amount}</li>
              ))}
            </ul>
              <p>{method}</p>
          </div>
          <div className='card-action'>
          <a class="waves-effect waves-light btn-small primary" onClick={() => setCurrent(recipe)}>Edit</a>
          <a class="waves-effect waves-light btn-small secondary" onClick={onDelete}>Delete</a>
          </div>
        </div>
      </div>
    </div>
  );
};

RecipeItem.prototype = {
  recipe: PropTypes.object,
};

export default RecipeItem;
